import org.apache.nifi.processor.io.StreamCallback
import java.nio.charset.StandardCharsets
import groovy.json.JsonSlurper
import groovy.json.JsonOutput

import java.time.Instant
import java.time.ZoneId
import java.time.ZonedDateTime
import java.time.format.DateTimeFormatter

log.info("Script started")

def flowFile = session.get()
if (!flowFile) {
    log.info("No flow file received, exiting")
    return
}

def isEmpty = false

flowFile = session.write(flowFile, { inputStream, outputStream ->
    log.info("Writing to flow file")

    def parser = new JsonSlurper().parse(inputStream)

    if (parser instanceof List && parser.isEmpty()) {
        log.info("Parsed data is an empty list")
        isEmpty = true
        return
    }

    def tunisZone = ZoneId.of("Africa/Tunis")
    def nowInstant = Instant.now()
    def zonedNow = nowInstant.atZone(tunisZone)
    def nowMillisTunis = zonedNow.toInstant().toEpochMilli()

    log.info("Current timestamp in Tunis (ingestion_date): " + nowMillisTunis)

    parser.each { record ->
        log.info("Processing record: " + record)

        try {
            def hexTs = record['_id']?.substring(0, 8)
            log.info("Processing record with _id: ${record['_id']}, hex timestamp: ${hexTs}")

            if (hexTs) {
                def epochSec = Long.parseLong(hexTs, 16)
                def utcInstant = Instant.ofEpochSecond(epochSec)
                def tunisZoned = utcInstant.atZone(tunisZone)
                def timestampMillisTunis = tunisZoned.toInstant().toEpochMilli()
                record['first_seen_date'] = timestampMillisTunis
                log.info("Converted first_seen_date to Tunis millis: " + timestampMillisTunis)
            } else {
                record['first_seen_date'] = null
                log.info("No valid _id found, setting first_seen_date to null")
            }
        } catch (Exception e) {
            record['first_seen_date'] = null
            log.error("Error processing first_seen_date: ${e.message}", e)
        }

        log.info("Setting ingestion_date: ${nowMillisTunis}")
        record['ingestion_date'] = nowMillisTunis
        record['source_system'] = "mongodb"
    }

    def jsonOut = JsonOutput.toJson(parser)
    outputStream.write(jsonOut.getBytes(StandardCharsets.UTF_8))
    log.info("Data processing completed, output written")
} as StreamCallback)

if (isEmpty) {
    log.info("The flow file contained no records, removing flowFile")
    session.remove(flowFile)
} else {
    log.info("Transfer flowFile to success relationship")
    session.transfer(flowFile, REL_SUCCESS)
}

log.info("Script completed")

def flowFile = session.get()
if (!flowFile) return

def startCal = Calendar.getInstance()
startCal.set(2021, 0, 1, 0, 0, 0) // Jan 1, 2021
startCal.set(Calendar.MILLISECOND, 0)

def endCal = Calendar.getInstance()
endCal.set(2025, 5, 1, 0, 0, 0) // June 1, 2025
endCal.set(Calendar.MILLISECOND, 0)

def loopCal = (startCal.clone() as Calendar)

while (loopCal.before(endCal)) {
    def startTimestamp = (loopCal.getTimeInMillis() / 1000) as long

    def nextCal = (loopCal.clone() as Calendar)
    nextCal.add(Calendar.MONTH, 1)
    def endTimestamp = (nextCal.getTimeInMillis() / 1000) as long

    def startOid = String.format("%08x0000000000000000", startTimestamp)
    def endOid = String.format("%08x0000000000000000", endTimestamp)

    def newFlowFile = session.create(flowFile)
    newFlowFile = session.putAttribute(newFlowFile, "database_name", flowFile.getAttribute("database_name")) 
    newFlowFile = session.putAttribute(newFlowFile, "collection_name", flowFile.getAttribute("collection_name"))
    newFlowFile = session.putAttribute(newFlowFile, "start_date_uid", startOid)
    newFlowFile = session.putAttribute(newFlowFile, "end_date_uid", endOid)

    // Add the year and month to the attributes
    newFlowFile = session.putAttribute(newFlowFile, "year", loopCal.get(Calendar.YEAR).toString())
    newFlowFile = session.putAttribute(newFlowFile, "month", (loopCal.get(Calendar.MONTH) + 1).toString())  // months are zero-indexed

    session.transfer(newFlowFile, REL_SUCCESS)

    loopCal = nextCal // move to next month
}

session.remove(flowFile)

