import org.apache.nifi.processor.io.InputStreamCallback
import org.apache.nifi.processor.io.StreamCallback
import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.nio.charset.StandardCharsets
import java.text.SimpleDateFormat
import java.util.TimeZone

// 1) Retrieve the FlowFile
def flowFile = session.get()
if (flowFile == null) return

// 2) Lookup Relationships by name
try {
    // Read JSON input as array
    def jsonInput = new StringBuilder()
    session.read(flowFile, { inputStream ->
        inputStream.withReader(StandardCharsets.UTF_8.name()) { reader ->
            jsonInput << reader.text
        }
    } as InputStreamCallback)

    def inputRecords = new JsonSlurper().parseText(jsonInput.toString())

    // Prepare outputs
    def allBundles = []
    def allPriceHistories = []
    def failedRecords = []

    inputRecords.each { orig ->
        try {
            def (bundleJson, priceHistoryJson) = processBundle(JsonOutput.toJson([orig]), orig)
            def bundle = new JsonSlurper().parseText(bundleJson)[0]
            def priceHistory = new JsonSlurper().parseText(priceHistoryJson)
            allBundles << bundle
            allPriceHistories.addAll(priceHistory)
        } catch (Exception e) {
            failedRecords << [ record: orig, error: e.message ]
            log.error("Failed processing record: ${e.message}", e)
        }
    }

    // Attributes to carry over
    def inherited = ['filename','filepath','database_name','collection_name']

    // 3) Function to write new FlowFiles
    def writeFF = { data, relationship, tableName ->
        if (!data) return
        def outFF = session.create()
        inherited.each { k ->
            def v = flowFile.getAttribute(k)
            if (v) outFF = session.putAttribute(outFF, k, v)
        }
        def json = JsonOutput.toJson(data)
        def bytes = json.getBytes(StandardCharsets.UTF_8)
        outFF = session.write(outFF, { os -> os.write(bytes) } as StreamCallback)
        outFF = session.putAttribute(outFF, 'file.size', bytes.length.toString())
        outFF = session.putAttribute(outFF, 'target_iceberg_table_name', tableName)
        session.transfer(outFF, relationship)
    }

    // 4) Emit to relationships
    writeFF(allBundles,      REL_SUCCESS, 'bundles')
    writeFF(allPriceHistories,REL_SUCCESS, 'bundles_price_history')
    if (failedRecords) {
        writeFF(failedRecords, REL_FAILURE, 'failed_records')
    }

    // Remove original
    session.remove(flowFile)

} catch (Exception e) {
    log.error("Error processing FlowFile", e)
    // Route original to failure
    session.transfer(session.putAttribute(flowFile, 'transformation.error', e.message), REL_FAILURE)
}

/**
 * processBundle(jsonArray, originalRecord) => [bundleJson, priceHistoryJson]
 */
def processBundle(String jsonArray, Map orig) {
    def data = new JsonSlurper().parseText(jsonArray)[0]
    def errors = []

    // Preserve your static override of price_history dates (unchanged)
    try {
        def rawDate = '2023-10-06'
        def df = new SimpleDateFormat('yyyy-MM-dd'); df.setTimeZone(TimeZone.getTimeZone('UTC'))
        def parsed = df.parse(rawDate)
        def iso = new SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss.SSS'Z'"); iso.setTimeZone(TimeZone.getTimeZone('UTC'))
        def overrideISO = iso.format(parsed)
        data.price_history?.each { rec ->
            if (rec.price != null && rec.date instanceof Map) {
                rec.date['$date'] = overrideISO
            }
        }
    } catch(_) { /* ignore */ }

    // Helper: nested get
    def get = { String path ->
        path.split(/\./).inject(data) { o,k -> (o instanceof Map)? o[k] : null }
    }

    // Extract & validate fields (unchanged logic)
    def name = get('name')
    if (!(name instanceof String && name.trim())) errors << 'name is required'

    def bundleIdRaw = get('bundleId')
    if (bundleIdRaw == null) errors << 'bundleId is required'
    def bundleid = bundleIdRaw.toString()

    def dataAmt = get('content.data.amount') ?: 0
    def dataUnit = get('content.data.unit') ?: 'Megabytes'
    def data_amount_gb = (dataUnit.toLowerCase() == 'megabytes') ? dataAmt/1000.0 : dataAmt

    def voiceAmt = get('content.voice.amount') ?: 0
    def voiceUnit= get('content.voice.unit') ?: 'Minutes'
    def voice_amount_minutes = (voiceUnit.toLowerCase()=='hours')? voiceAmt*60 : voiceAmt

    def sms_amount = get('content.sms') ?: 0

    def priceAmt = get('price.amount') ?: 0
    if (!(priceAmt instanceof Number && priceAmt>=1.0 && priceAmt<=60.0)) errors << 'price.amount out of range'

    def validity_days = get('validity.number')
    if (!(validity_days instanceof Number && validity_days>=0 && validity_days<=300)) errors << 'validity.number out of range'

    // Parse createdAt as millis
    def createdRaw = get('createdAt')
    Long createdat = null
    if (createdRaw?.toString()?.isLong()) createdat = createdRaw.toLong()
    if (!createdat) errors << 'createdAt invalid'

    if (errors) {
        log.error("Validation errors: ${errors}")
        throw new Exception(errors.join('; '))
    }

    // Build bundle record and include metadata fields
    def nowMs = System.currentTimeMillis()
    def bundleObj = [
        id                    : data._id,
        name                  : name,
        bundleid              : bundleid,
        data_amount_gb        : data_amount_gb,
        voice_amount_minutes  : voice_amount_minutes,
        sms_amount            : sms_amount,
        validity_days         : validity_days,
        createdat             : createdat,
        first_seen_date       : orig.first_seen_date as Long,
        ingestion_date        : orig.ingestion_date as Long,
        transformation_date   : nowMs,
        source_system         : orig.source_system
    ]

    // Price history (input.price_history is now null or list)
    def hist = data.price_history ?: []
    def histList = []
    if (hist instanceof List) {
        hist.each { rec ->
            def raw = rec?.date
            if (raw?.toString()?.isLong()) {
                histList << [bundleid: bundleid, price: rec.price, date: new Date(raw.toLong())]
            }
        }
    }
    histList.sort{ a,b -> a.date <=> b.date }

    // Build history array
    def priceArray = []
    histList.eachWithIndex{ entry, idx ->
        def start = (idx==0)? new Date(createdat) : histList[idx-1].date
        def end   = entry.date
        if (start.after(end)) start = end
        priceArray << [
            bundleid   : bundleid,
            price      : entry.price,
            start_date : start.time,
            end_date   : end.time
        ]
    }
    if (!histList) {
        priceArray << [ bundleid:bundleid, price:priceAmt, start_date:createdat, end_date:253402300799000 ]
    } else {
        def last = histList[-1].date
        priceArray << [ bundleid:bundleid, price:priceAmt, start_date:last.time, end_date:253402300799000 ]
    }

    // Return JSON strings
    def bundleJson       = JsonOutput.prettyPrint(JsonOutput.toJson([bundleObj]))
    def priceHistoryJson = JsonOutput.prettyPrint(JsonOutput.toJson(priceArray))
    return [bundleJson, priceHistoryJson]
}

import org.apache.iceberg.Table
import org.apache.iceberg.Schema
import org.apache.iceberg.catalog.Catalog
import org.apache.iceberg.catalog.TableIdentifier
import org.apache.iceberg.rest.RESTCatalog
import org.apache.iceberg.io.DataWriter
import org.apache.iceberg.data.GenericRecord
import org.apache.iceberg.data.Record
import org.apache.iceberg.parquet.Parquet
import org.apache.iceberg.types.Types
import org.apache.iceberg.relocated.com.google.common.collect.ImmutableMap
import com.fasterxml.jackson.databind.ObjectMapper
import java.util.UUID
// Cached Iceberg components
class IcebergResources {
    static Catalog catalog
    static Table table

    static synchronized void initialize() {
        if (catalog == null) {
            // 1. Configure MinIO/Iceberg
            def config = ImmutableMap.builder()
                .put("uri", "http://iceberg-rest:8181")
                .put("warehouse", "s3://warehouse")
                .put("s3.endpoint", "http://minio:9000")
                .put("s3.access-key-id", "minioadmin")
                .put("s3.secret-access-key", "minioadmin")
                .put("s3.path-style-access", "true")
                .put("io-impl", "org.apache.iceberg.aws.s3.S3FileIO")
                .build()

            // 2. Initialize REST Catalog
            catalog = new RESTCatalog()
            catalog.initialize("rest", config)

            // 3. Load table
            table = catalog.loadTable(TableIdentifier.of("default", "my_table"))
        }
    }
}

// Initialize once
IcebergResources.initialize()

def flowFile = session.get()
if (!flowFile) return

try {
    // 4. Read JSON and convert to Iceberg Records
    def json = flowFile.read().getText("UTF-8")
    def records = parseJsonToRecords(json, IcebergResources.table.schema())

    // 5. Write to Parquet
    def outputFile = IcebergResources.table.io().newOutputFile("data-${UUID.randomUUID()}.parquet")
    DataWriter<Record> writer = Parquet.writeData(outputFile)
        .schema(IcebergResources.table.schema())
        .createWriterFunc(org.apache.iceberg.parquet.ParquetWriter::buildWriter)
        .build()

    try {
        records.each { writer.write(it) }
    } finally {
        writer.close()
    }

    // 6. Commit to Iceberg
    IcebergResources.table.newAppend()
        .appendFile(writer.toDataFile())
        .commit()

    session.transfer(flowFile, REL_SUCCESS)
} catch (Exception e) {
    log.error("Iceberg append failed: ${e.message}", e)
    session.transfer(flowFile, REL_FAILURE)
}

// JSON to Iceberg Records
List<Record> parseJsonToRecords(String json, Schema schema) {
    def mapper = new ObjectMapper()
    def tree = mapper.readTree(json)
    def records = []

    tree.each { node ->
        Record record = GenericRecord.create(schema)
        schema.columns().each { col ->
            def value = node.get(col.name())?.asText()
            record.setField(col.name(), parseValue(value, col.type()))
        }
        records << record
    }
    return records
}

// Parse value based on Iceberg type
def parseValue(String value, Types.NestedField field) {
    if (value == null) return null
    switch (field.type().typeId()) {
        case Types.Type.TypeID.INTEGER:
            return Integer.parseInt(value)
        case Types.Type.TypeID.LONG:
            return Long.parseLong(value)
        case Types.Type.TypeID.BOOLEAN:
            return Boolean.parseBoolean(value)
        case Types.Type.TypeID.DOUBLE:
            return Double.parseDouble(value)
        default:
            return value
    }
}
